function lookscheckInternetStatus(url,offlinemessage,onlinemessage){$.ajax({url:url||"https://fonts.googleapis.com/icon?family=Material+Icons",type:"GET",cache:!1,success:function(data,status,jqXHR){var message=onlinemessage||"You are online now.";document.getElementById("looks-internet-status").className="looks-online-box";$("#looks_status_content").text(message);$("#looks_retry_button").fadeOut();setTimeout(function(){$("#looks-internet-status").fadeOut();$("#looks_status_content").text('')},3000)},error:function(jqXHR,status,err){var message=offlinemessage||"Looks like you are offline.";$("#looks_retry_button").show();$("#looks-internet-status").fadeIn();setTimeout(function(){$("#looks_status_content").text(message)},1000);document.getElementById("looks-internet-status").className="looks-offline-box"}})};function looksOffline(looks){$("body").append("<div id='looksOverlay'></div>");var photo=looks.photo||"retry.png";var append='<div id="looks_circle_internet"></div><div id="looks_status_content" class="looks_status_content"></div><div id="looks_retry_button"><img src="'+photo+'" width="20px"></div>';$("#looks-internet-status").html(append);if(looks.key){lookscheckInternetStatus(looks.url,looks.offlinemessage,looks.onlinemessage)};setInterval(function(){lookscheckInternetStatus(looks.url,looks.offlinemessage,looks.onlinemessage)},looks.timer||10000)};$("#looks_retry_button").on("click",function(){$(".looks_status_content").html("Attempting to reconnect...");lookscheckInternetStatus()})